mapper.select:
select ORDER_NO, AMOUNT, PAY_STATUS, decode(PAY_TYPE,'VSP501','微信','VSP511','支付宝',PAY_TYPE) PAY_TYPE, PAY_TIME, TENANT_NAME, TENANT_PHONE, AREA_ID, AREA, STALL, RENT_START_TIME, RENT_END_TIME, TRX_ID, CHANNEL_TRX_ID, OPEN_ID, INSERT_TIME, UPDATE_TIME, decode(PERIOD,'1','一天','4','一月','12','一季',PERIOD) PERIOD, decode(CONTINUE_RENT,'0','已续租','1','未续租',CONTINUE_RENT) CONTINUE_RENT from T_GWJ_ORDER
and ORDER_NO = #{orderNo}
and TENANT_NAME like '%' || #{tenantName} || '%'
and PERIOD = #{period}
and AREA_ID = #{areaId}
and STALL like '%' || #{stall} || '%'
and pay_status = '0000' order by pay_time desc

mapper.update:
update T_GWJ_ORDER set RENT_END_TIME =
when #{item.orderNo} then #{item.rentEndTime}
where ORDER_NO in
#{item.orderNo}
